import React from "react";
import Table from "react-bootstrap/Table";
import axios from "axios";
import { useState ,useEffect} from "react";
import './purchase.css';

export default function Purchase() {
  const [items, setItems] = useState([]);
  const [counter, setCounter] = useState(2);
  const[category,setCategory]=useState("");
  const[vendor,setVendor]=useState("");
  const[amount,setAmount]=useState("");

  useEffect(() => {
    axios
      .get("http://localhost:5000/purchase/getItems")
      .then(function (response) {
        setItems(response.data);
      })
      .catch(function (error) {
        console.log(error);
      })
      .finally(function () {
      });
  }, []);

  
  const totAmount = (e) => {
    let amountkg=document.getElementById("amount").value;
    let quantity=document.getElementById("quantity").value;
    console.log(amountkg,quantity);
    setAmount(amountkg*quantity);
  };
  function a(l){
   
    setCategory(l.category)
    setVendor(l.vendorName)

  }
  const getCategory=async (e)=>{
    let item=document.getElementById("item").value;    
    axios
      .post("http://localhost:5000/purchase/getCategoryVendor", {
        item:item,
      })
      .then(function (response) {
        a(response.data[0])
      })
      .catch(function (error) {
        console.log(error);
      });
  }
  
  const submit = () => {
    let item=document.getElementById("item").value;    
    let date=document.getElementById("date").value;
    let category=document.getElementById("category")
axios.post("http://localhost:5000/purchase/add", {
  date:date,
  item:item,
  category:category,
  quantity:quantity,
  amountkg:amountkg,
  amount:amount
})
  };

  const generateRows = () => {
    let n=document.getElementById("num").value;
  for(let i=1;i<=n;i++){
  let x = document.getElementById("table");
    let row = x.insertRow();

    let cell1 = row.insertCell();
    let cell2 = row.insertCell();
    let cell3 = row.insertCell();
    let cell4 = row.insertCell();
    let cell5 = row.insertCell();
    let cell6 = row.insertCell();
    let cell7 = row.insertCell();

    //select

    const select = document.createElement("Select");
    select.setAttribute("class", "form-select");
    const option = document.createElement("option");
    const optionText = document.createTextNode("Select");
    option.appendChild(optionText);
    option.setAttribute("value", "select");
    select.setAttribute("id", counter);

    select.appendChild(option);

    for (let i = 1; i < items.length; i++) {
      const option = document.createElement("option");
      const optionText = document.createTextNode(items[i].item);
      option.appendChild(optionText);
      option.setAttribute("value", items[i].item);
      select.appendChild(option);
    }

    cell2.appendChild(select);

    // totquantity

    let input1 = document.createElement("input");
    input1.setAttribute("type", "number");
    input1.setAttribute("placeholder", "Category");
    input1.setAttribute("class", "form-control");
    input1.setAttribute("id", counter + " totquantity");
    input1.disabled = true;

    cell3.appendChild(input1);

    //current Quantity

    let input2 = document.createElement("input");
    input2.setAttribute("type", "number");
    input2.setAttribute("placeholder", "Vendor");
    input2.setAttribute("class", "form-control");
    input2.setAttribute("id", counter + " currquantity");
    input2.disabled = true;

    cell4.appendChild(input2);

    //RMK

    let input3 = document.createElement("input");
    input3.setAttribute("type", "number");
    input3.setAttribute("placeholder", "Quantity");
    input3.setAttribute("class", "form-control");
    input3.setAttribute("id", counter + " RMK");
  

    cell5.appendChild(input3);

    //RMD

    let input4 = document.createElement("input");
    input4.setAttribute("type", "number");
    input4.setAttribute("placeholder", "Amount");
    input4.setAttribute("class", "form-control");
    input4.setAttribute("id", counter + " RMD");

    cell6.appendChild(input4);

    let input5 = document.createElement("input");
    input5.setAttribute("type", "number");
    input5.setAttribute("placeholder", "Total Amount");
    input5.setAttribute("class", "form-control");
    input5.setAttribute("id", counter + " RMKCET");
    input5.disabled=true;

    cell7.appendChild(input5);

    cell1.innerHTML = counter;

    setCounter(counter + 1);
}
}

return (
    <div>
      <h1 style={{ marginTop: "2%" }}>Enter Purchase Details</h1>
      <div className="container" style={{ marginTop: "3%" }}>
      <div className="col">
        <div className="left">
          <label for="date" id="date-label">Date:</label>
          <input type="date" id="date" name="date" />
        </div>
        <div className="right">
          <labell for="number" id="row">Enter the number of rows : </labell>
          <input type="number" id="num" />
          <button class="btn btn-primary" id="add-btn" onClick={generateRows}>Add</button>

        </div>
      </div>
      </div>
        <Table striped bordered hover id="table">
          <thead>
            <tr>
              <th>SNo</th>
              <th>Select Item</th>
              <th>Category</th>
              <th>Vendor</th>
              <th>Quantity</th>
              <th>Amount</th>
              <th>Total Amount</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>1</td>
              <td>
                <select
                  class="form-select"
                  aria-label="Default select example"
                  onChange={getCategory}
                  id="item"
                >
                  <option selected>Select</option>

                  {items.map((item, idx) => {
                    return (
                      <option key={idx} value={item.item}>
                        {item.item}
                      </option>
                    );
                  })}
                </select>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Category"
                    id="category"
                    disabled
                    value={category}
                  />
                </div>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Vendor"
                    id="vendor"
                    disabled
                    value={vendor} 
                  />
                </div>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="number"
                    class="form-control"
                    placeholder="Quantity"
                    id="quantity"
                  />
                </div>
              </td>

              <td>
                <div class="input-group mb-3">
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Amount"
                    onChange={totAmount}
                    id="amount"
                  />
                </div>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="number"
                    class="form-control"
                    placeholder="Total Amount"
                    id="total"
                    disabled
                    value={amount}
                  />
                </div>
              </td>
            </tr>
          </tbody>
        </Table>
        <br />
        <button class="btn btn-primary" id="submit-btn" onClick={submit}>Submit</button>
      </div>
    
  );
}
