}
}

  
  return (
    <div>
      <h1 style={{ marginTop: "2%" }}>Enter Purchase Details</h1>
      <div className="container" style={{ marginTop: "3%" }}>
      <div className="col">
        <div className="left">
          <label for="date" id="date-label">Date:</label>
          <input type="date" id="date" name="date" />
        </div>
        <div className="right">
          <labell for="number" id="row">Enter the number of rows : </labell>
          <input type="number" id="num" />
          <button class="btn btn-primary" id="add-btn" onClick={generateRows}>Add</button>

        </div>
      </div>
      </div>
        <Table striped bordered hover id="table">
          <thead>
            <tr>
              <th>SNo</th>
              <th>Select Item</th>
              <th>Category</th>
              <th>Vendor</th>
              <th>Quantity</th>
              <th>Amount</th>
              <th>Total Amount</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>1</td>
              <td>
                <select
                  class="form-select"
                  aria-label="Default select example"
                  id="item"
                >
                  <option selected>Select</option>

                  {items.map((item, idx) => {
                    return (
                      <option key={idx} value={item.item}>
                        {item.item}
                      </option>
                    );
                  })}
                </select>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="number"
                    class="form-control"
                    placeholder="Category"
                    id="category"
                    disabled
                    onChange={getCategory}
                  />
                </div>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="number"
                    class="form-control"
                    placeholder="Vendor"
                    id="vendor"
                    disabled
                    onChange={getVendor}
                  />
                </div>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="number"
                    class="form-control"
                    placeholder="Quantity"
                    id="quantity"
                  />
                </div>
              </td>

              <td>
                <div class="input-group mb-3">
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Amount"
                    id="amount"
                  />
                </div>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="number"
                    class="form-control"
                    placeholder="Total Amount"
                    id="total"
                    onChange={totAmount}
                    disabled
                  />
                </div>
              </td>
            </tr>
          </tbody>
        </Table>
        <br />
        <button class="btn btn-primary" id="submit-btn" onClick={submit}>Submit</button>
      </div>
    
  );
}
