import React from "react";
import Table from "react-bootstrap/Table";
import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";

export default function Purchase() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/dispatch/retrive")
      .then(function (response) {
        // handle success

        setItems(response.data);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
      .finally(function () {
        // always executed
      });
  }, []);

  const click = ()=>{
    let x = document.getElementById('1 quantity').value;
    console.log(x);
  }


  const getQuantity = async (e)=>{
    let itemName = e.target.value;
    console.log(itemName);
    let quantity = document.getElementById('1 quantity');
    axios.post('http://localhost:5000/dispatch/getQuantity', {
      itemName: itemName
      
    })
    .then(function (response) {
      console.log(response.data[0].quantity);
      quantity.value = response.data[0].quantity;
    })
    .catch(function (error) {
      console.log(error);
    });
  }


  return (
    <div>
      <div className="container" style={{ marginTop: "3%" }}>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Select</th>
              <th>Category</th>
              <th>Vendor</th>
              <th>Quantity</th>
              <th>Amount</th>
              <th>Total Amount</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>1</td>
              <td>
                <select class="form-select" aria-label="Default select example" onChange={getQuantity}>
                  <option selected>Select</option>

                  {items.map((item, idx) => {
                    return (
                      <option key={idx} value={item.item}>
                        {item.item}
                      </option>
                    );
                  })}

                </select>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="text"
                    class="form-control"
                    placeholder=""
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    id="1 quantity" 
                    defaultValue={0}
                  />
                </div>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Recipient's username"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                  />
                </div>
              </td>

              <td>
                <div class="input-group mb-3">
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Recipient's username"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                  />
                </div>
              </td>
              <td>
                <div class="input-group mb-3">
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Recipient's username"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                  />
                </div>
              </td>
            </tr>
          </tbody>
        </Table>
        
        <button onClick={click} >Click me</button>

      </div>
    </div>
  );
}
